mapper.select:
select user_id userId,user_name userName,a.jgid jgid,user_name userName, a.status status,password password,user_age userAge,createTime createTime,roleId roleId,c.ORGANIZSECONDNAME jgmc from t_user a left join t_user_role b on a.user_name = b.userName and a.jgid = b.jgid left join sys_organization c on a.jgid = c.jgid where a.status=1
and user_id=#{userId}
and user_name=#{userName}
order by user_id,createTime

select a.id userId, a.name userName, b.organizcode jgid, a.status status, a.password password, a.createdt createTime, c.roleid roleId, d.organizsecondname jgmc from base_user a left join sys_personnel b on a.id = b.personid left join t_user_role c on a.id = c.userid and b.organizcode = c.jgid left join sys_organization d on c.jgid = d.organizcode where a.status = 1
and a.id =#{userId}
and a.name =#{userName}
order by a.id,c.roleid, a.createdt

select a.user_id userId,a.user_name userName,a.password password,a.user_age userAge,a.createTime createTime,b.jgid jgid ,b.ORGANIZNAME jgmc,c.roleId as roleId,a.status status
from t_user a left join sys_organization b on a.jgid = b.jgid left JOIN t_user_role c on a.user_name = c.userName and a.jgid = c.jgid   where 1=1

    and user_id=#{userId}


    and user_name=#{userName}

order by user_id,createTime

select a.id userId, a.name userName, a.password password, a.createDt createTime, e.organizcode jgid, b.ORGANIZNAME jgmc, c.roleId as roleId, a.status status from base_user a left join sys_personnel e on a.id = e.personid left join sys_organization b on e.organizcode = b.organizcode left JOIN t_user_role c on a.id = c.userid and e.organizcode = c.jgid where 1 = 1
and id = #{userId}
and name = #{userName}
order by id, createDt

select a.user_id userId,a.user_name userName,a.user_age userAge,a.createTime createTime,b.jgid jgid ,b.ORGANIZNAME jgmc,c.roleId as roleId,a.status status ,group_concat(d.ORGANIZNAME separator ',') jgmcQX from t_user a left join sys_organization b on a.jgid = b.jgid left JOIN t_user_role c on a.user_name = c.userName left join sys_organization d on c.jgid = d.jgid where 1=1
and user_id=#{userId}
and user_name=#{userName}
GROUP BY a.user_id ,a.user_name ,a.user_age ,a.createTime ,b.jgid ,b.ORGANIZNAME ,c.roleId ,a.status order by user_id,createTime

select a.id userId, a.name userName, a.createdt createTime, e.organizcode jgid, b.ORGANIZNAME jgmc, c.roleId as roleId, r.roledesc roleDesc, a.status status, WMSYS.WM_CONCAT(d.ORGANIZNAME ) jgmcQX from base_user a left join sys_personnel e on a.id = e.personid left join sys_organization b on e.organizcode= b.organizcode left JOIN t_user_role c on a.id = c.userid left join t_role r on r.id=c.roleid left join sys_organization d on c.jgid = d.organizcode
and a.id = #{userId}
and a.name like concat(concat('%',#{userName}),'%')
and e.organizcode = #{jgid}
GROUP BY a.id, a.name, a.createdt, e.organizcode, b.ORGANIZNAME, c.roleId, r.roledesc, a.status order by a.id, a.createdt

select roleId roleId,jgid jgid,userName userName,b.roleDesc roleName
from t_user_role a left join t_role b on a.roleId = b.id
 where   userName=#{userName}

mapper.update:
update t_role


        roleDesc=#{roleDesc},


        status=#{status}


where id=#{id}

update base_user


        password=#{password},


        status=#{status}


where id=#{userId}
